import React from 'react';
import QRCode from 'react-qr-code';
import './QRCodeGenerator.css';

const QRCodeGenerator = ({ url = 'http://localhost:3000', title = 'Berber Uygulamasƒ±' }) => {
  const handleDownload = () => {
    const svg = document.getElementById('qr-code-svg');
    const svgData = new XMLSerializer().serializeToString(svg);
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    const img = new Image();

    img.onload = () => {
      canvas.width = img.width;
      canvas.height = img.height;
      ctx.drawImage(img, 0, 0);

      const pngFile = canvas.toDataURL('image/png');
      const downloadLink = document.createElement('a');
      downloadLink.download = 'berber-app-qr.png';
      downloadLink.href = pngFile;
      downloadLink.click();
    };

    img.src = 'data:image/svg+xml;base64,' + btoa(svgData);
  };

  const handlePrint = () => {
    const printWindow = window.open('', '_blank');
    const qrCodeElement = document.getElementById('qr-code-container').innerHTML;

    printWindow.document.write(`
      <html>
        <head>
          <title>QR Kod - ${title}</title>
          <style>
            body { 
              font-family: Arial, sans-serif; 
              text-align: center; 
              padding: 20px;
            }
            .qr-container {
              display: flex;
              flex-direction: column;
              align-items: center;
              gap: 20px;
            }
            .qr-title {
              font-size: 24px;
              font-weight: bold;
              color: #333;
            }
            .qr-url {
              font-size: 14px;
              color: #666;
              word-break: break-all;
            }
          </style>
        </head>
        <body>
          <div class="qr-container">
            <div class="qr-title">${title}</div>
            ${qrCodeElement}
            <div class="qr-url">${url}</div>
          </div>
        </body>
      </html>
    `);

    printWindow.document.close();
    printWindow.print();
  };

  return (
    <div className="qr-code-generator">
      <div className="qr-header">
        <h3>QR Kod</h3>
        <p>Bu QR kodu tarayarak uygulamaya hƒ±zlƒ± eri≈üim saƒülayabilirsiniz</p>
      </div>

      <div id="qr-code-container" className="qr-container">
        <div className="qr-title">{title}</div>
        <QRCode
          id="qr-code-svg"
          value={url}
          size={200}
          style={{ height: "auto", maxWidth: "100%", width: "100%" }}
          viewBox={`0 0 200 200`}
        />
        <div className="qr-url">{url}</div>
      </div>

      <div className="qr-actions">
        <button onClick={handleDownload} className="qr-btn download-btn">
          üì± ƒ∞ndir
        </button>
        <button onClick={handlePrint} className="qr-btn print-btn">
          üñ®Ô∏è Yazdƒ±r
        </button>
      </div>
    </div>
  );
};

export default QRCodeGenerator;
