import React, { useState, useEffect } from 'react';
import './BusinessDashboard.css';

const BusinessDashboard = () => {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('ALL');
  const [businessOwnerId, setBusinessOwnerId] = useState(1); // Geçici olarak 1 kullanıyoruz

  // Randevuları getir
  const fetchAppointments = async () => {
    try {
      setLoading(true);
      let url;
      
      if (filter !== 'ALL') {
        url = `http://localhost:8080/appointments/business/${businessOwnerId}/status/${filter}`;
      } else {
        url = `http://localhost:8080/appointments/business/${businessOwnerId}`;
      }
      
      const response = await fetch(url);
      if (response.ok) {
        const data = await response.json();
        setAppointments(data);
      } else {
        console.error('Randevular yüklenirken hata oluştu');
      }
    } catch (error) {
      console.error('API hatası:', error);
    } finally {
      setLoading(false);
    }
  };

  // Randevu durumunu güncelle
  const updateAppointmentStatus = async (appointmentId, newStatus) => {
    try {
      const response = await fetch(`http://localhost:8080/appointments/${appointmentId}/status?status=${newStatus}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      
      if (response.ok) {
        // Randevuları yeniden yükle
        fetchAppointments();
        alert('Randevu durumu güncellendi!');
      } else {
        alert('Durum güncellenirken hata oluştu');
      }
    } catch (err) {
      alert('Bağlantı hatası: ' + err.message);
    }
  };

  // Component yüklendiğinde randevuları getir
  useEffect(() => {
    fetchAppointments();
  }, [filter, businessOwnerId]);

  // WebSocket bağlantısı (gerçek zamanlı güncellemeler için)
  useEffect(() => {
    // SockJS ve STOMP kullanarak WebSocket bağlantısı
    const SockJS = require('sockjs-client');
    const { Client } = require('@stomp/stompjs');
    
    const client = new Client({
      webSocketFactory: () => new SockJS('http://localhost:8080/ws'),
      debug: (str) => console.log(str),
      onConnect: () => {
        console.log('WebSocket bağlantısı kuruldu');
        
        // İşletme sahibine özel bildirimler için subscribe
        client.subscribe(`/topic/notifications/${businessOwnerId}`, (message) => {
          const notification = JSON.parse(message.body);
          if (notification.type === 'NEW_APPOINTMENT') {
            alert('Yeni randevu alındı!');
            fetchAppointments();
          }
        });
      },
      onStompError: (error) => {
        console.error('WebSocket bağlantı hatası:', error);
      }
    });
    
    client.activate();
    
    return () => {
      if (client && client.connected) {
        client.deactivate();
      }
    };
  }, [businessOwnerId]);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('tr-TR');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'PENDING': return '#ffa500';
      case 'CONFIRMED': return '#28a745';
      case 'COMPLETED': return '#6c757d';
      case 'CANCELLED': return '#dc3545';
      default: return '#007bff';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'PENDING': return 'Beklemede';
      case 'CONFIRMED': return 'Onaylandı';
      case 'COMPLETED': return 'Tamamlandı';
      case 'CANCELLED': return 'İptal Edildi';
      default: return status;
    }
  };

  if (loading) {
    return <div className="loading">Randevular yükleniyor...</div>;
  }

  if (error) {
    return <div className="error">Hata: {error}</div>;
  }

  return (
    <div className="business-dashboard">
      <header className="dashboard-header">
        <h1>İşletme Yönetim Paneli</h1>
        <p>Randevularınızı yönetin ve takip edin</p>
      </header>

      <div className="dashboard-controls">
        <div className="filter-section">
          <label htmlFor="status-filter">Durum Filtresi:</label>
          <select 
            id="status-filter"
            value={filter} 
            onChange={(e) => setFilter(e.target.value)}
            className="filter-select"
          >
            <option value="ALL">Tümü</option>
            <option value="PENDING">Beklemede</option>
            <option value="CONFIRMED">Onaylandı</option>
            <option value="COMPLETED">Tamamlandı</option>
            <option value="CANCELLED">İptal Edildi</option>
          </select>
        </div>
        
        <button 
          onClick={fetchAppointments}
          className="refresh-btn"
        >
          🔄 Yenile
        </button>
      </div>

      <div className="appointments-section">
        <h2>Randevular ({appointments.length})</h2>
        
        {appointments.length === 0 ? (
          <div className="no-appointments">
            <p>Henüz randevu bulunmuyor.</p>
          </div>
        ) : (
          <div className="appointments-grid">
            {appointments.map((appointment) => (
              <div key={appointment.id} className="appointment-card">
                <div className="appointment-header">
                  <span 
                    className="status-badge"
                    style={{ backgroundColor: getStatusColor(appointment.status) }}
                  >
                    {getStatusText(appointment.status)}
                  </span>
                  <span className="appointment-id">#{appointment.id}</span>
                </div>
                
                <div className="appointment-details">
                  <h3>{appointment.customerName}</h3>
                  <p><strong>Telefon:</strong> {appointment.customerPhone}</p>
                  <p><strong>Tarih:</strong> {formatDate(appointment.appointmentDate)}</p>
                  <p><strong>Hizmetler:</strong> {appointment.services?.join(', ')}</p>
                  <p><strong>Toplam Fiyat:</strong> ₺{appointment.totalPrice}</p>
                  {appointment.notes && (
                    <p><strong>Notlar:</strong> {appointment.notes}</p>
                  )}
                </div>
                
                <div className="appointment-actions">
                  {appointment.status === 'PENDING' && (
                    <>
                      <button 
                        onClick={() => updateAppointmentStatus(appointment.id, 'CONFIRMED')}
                        className="btn btn-confirm"
                      >
                        Onayla
                      </button>
                      <button 
                        onClick={() => updateAppointmentStatus(appointment.id, 'CANCELLED')}
                        className="btn btn-cancel"
                      >
                        İptal Et
                      </button>
                    </>
                  )}
                  
                  {appointment.status === 'CONFIRMED' && (
                    <>
                      <button 
                        onClick={() => updateAppointmentStatus(appointment.id, 'COMPLETED')}
                        className="btn btn-complete"
                      >
                        Tamamla
                      </button>
                      <button 
                        onClick={() => updateAppointmentStatus(appointment.id, 'CANCELLED')}
                        className="btn btn-cancel"
                      >
                        İptal Et
                      </button>
                    </>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default BusinessDashboard;
