import React, { useEffect, useRef } from 'react';
import QRCode from 'qrcode.js';
import './QRCodeGenerator.css';

const QRCodeGenerator = ({ url = 'http://localhost:3000', size = 200 }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    if (canvasRef.current) {
      const qr = new QRCode({
        text: url,
        width: size,
        height: size,
        colorDark: '#000000',
        colorLight: '#ffffff',
        correctLevel: QRCode.CorrectLevel.H
      });

      // Canvas'ı temizle
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // QR kodu çiz
      qr.makeCode(url);
      const qrCanvas = qr._oDrawing._elCanvas;
      ctx.drawImage(qrCanvas, 0, 0, size, size);
    }
  }, [url, size]);

  const downloadQR = () => {
    const canvas = canvasRef.current;
    const link = document.createElement('a');
    link.download = 'berber-app-qr.png';
    link.href = canvas.toDataURL();
    link.click();
  };

  return (
    <div className="qr-generator">
      <div className="qr-header">
        <h3>Berber App QR Kodu</h3>
        <p>Bu QR kodu tarayarak uygulamaya hızlı erişim sağlayabilirsiniz</p>
      </div>

      <div className="qr-container">
        <canvas 
          ref={canvasRef} 
          width={size} 
          height={size}
          className="qr-canvas"
        />
      </div>

      <div className="qr-info">
        <p className="qr-url">URL: {url}</p>
        <button onClick={downloadQR} className="download-btn">
          QR Kodu İndir
        </button>
      </div>
    </div>
  );
};

export default QRCodeGenerator;
