import React, { useState } from 'react';
import './BusinessOwnerForm.css';

const BusinessOwnerForm = () => {
  const [formData, setFormData] = useState({
    businessName: '',
    ownerName: '',
    ownerSurname: '',
    phone: '',
    email: '',
    address: '',
    city: '',
    district: '',
    postalCode: '',
    taxNumber: '',
    services: [],
    workingHours: {
      monday: { open: '09:00', close: '18:00', closed: false },
      tuesday: { open: '09:00', close: '18:00', closed: false },
      wednesday: { open: '09:00', close: '18:00', closed: false },
      thursday: { open: '09:00', close: '18:00', closed: false },
      friday: { open: '09:00', close: '18:00', closed: false },
      saturday: { open: '09:00', close: '18:00', closed: false },
      sunday: { open: '09:00', close: '18:00', closed: true }
    },
    description: ''
  });

  const availableServices = [
    { name: 'Saç Kesimi', price: 80 },
    { name: 'Sakal Tıraşı', price: 40 },
    { name: 'Saç Yıkama', price: 25 },
    { name: 'Saç Boyama', price: 150 },
    { name: 'Perma', price: 200 },
    { name: 'Saç Bakımı', price: 100 },
    { name: 'Kaş Düzeltme', price: 30 },
    { name: 'Yüz Bakımı', price: 120 }
  ];

  const days = {
    monday: 'Pazartesi',
    tuesday: 'Salı',
    wednesday: 'Çarşamba',
    thursday: 'Perşembe',
    friday: 'Cuma',
    saturday: 'Cumartesi',
    sunday: 'Pazar'
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleServiceChange = (service) => {
    setFormData(prev => ({
      ...prev,
      services: prev.services.find(s => s.name === service.name)
        ? prev.services.filter(s => s.name !== service.name)
        : [...prev.services, service]
    }));
  };

  const handleWorkingHoursChange = (day, field, value) => {
    setFormData(prev => ({
      ...prev,
      workingHours: {
        ...prev.workingHours,
        [day]: {
          ...prev.workingHours[day],
          [field]: value
        }
      }
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Hizmetleri Map formatına çevir
      const servicesMap = {};
      formData.services.forEach(service => {
        servicesMap[service.name] = service.price;
      });

      // Çalışma saatlerini Map formatına çevir
      const workingHoursMap = {};
      Object.keys(formData.workingHours).forEach(day => {
        const dayInfo = formData.workingHours[day];
        if (dayInfo.closed) {
          workingHoursMap[days[day]] = 'Kapalı';
        } else {
          workingHoursMap[days[day]] = `${dayInfo.open} - ${dayInfo.close}`;
        }
      });

      const businessOwnerData = {
        businessName: formData.businessName,
        ownerName: formData.ownerName,
        ownerSurname: formData.ownerSurname,
        phone: formData.phone,
        email: formData.email,
        address: formData.address,
        city: formData.city,
        district: formData.district,
        postalCode: formData.postalCode,
        taxNumber: formData.taxNumber,
        services: servicesMap,
        workingHours: workingHoursMap,
        description: formData.description
      };

      const response = await fetch('http://localhost:8080/business-owners', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(businessOwnerData)
      });

      if (response.ok) {
        const result = await response.json();
        alert('İşletme kaydı başarıyla oluşturuldu!');
        console.log('Kayıt başarılı:', result);

        // Formu sıfırla
        setFormData({
          businessName: '',
          ownerName: '',
          ownerSurname: '',
          phone: '',
          email: '',
          address: '',
          city: '',
          district: '',
          postalCode: '',
          taxNumber: '',
          services: [],
          workingHours: {
            monday: { open: '09:00', close: '18:00', closed: false },
            tuesday: { open: '09:00', close: '18:00', closed: false },
            wednesday: { open: '09:00', close: '18:00', closed: false },
            thursday: { open: '09:00', close: '18:00', closed: false },
            friday: { open: '09:00', close: '18:00', closed: false },
            saturday: { open: '09:00', close: '18:00', closed: false },
            sunday: { open: '09:00', close: '18:00', closed: true }
          },
          description: ''
        });
      } else {
        const errorData = await response.json();
        alert(`Hata: ${errorData.message || 'İşletme kaydı oluşturulamadı'}`);
      }
    } catch (error) {
      console.error('API Hatası:', error);
      alert('Bağlantı hatası! Lütfen tekrar deneyin.');
    }
  };

  return (
    <div className="business-form-container">
      <div className="business-form">
        <h1>İşletme Sahibi Kayıt Formu</h1>
        <p className="form-description">
          Berber salonunuzu kaydetmek için lütfen aşağıdaki bilgileri doldurun.
        </p>

        <form onSubmit={handleSubmit}>
          {/* İşletme Bilgileri */}
          <div className="form-section">
            <h2>İşletme Bilgileri</h2>

            <div className="form-group">
              <label htmlFor="businessName">İşletme Adı *</label>
              <input
                type="text"
                id="businessName"
                name="businessName"
                value={formData.businessName}
                onChange={handleInputChange}
                required
                placeholder="Berber Salonu Adı"
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="ownerName">Sahip Adı *</label>
                <input
                  type="text"
                  id="ownerName"
                  name="ownerName"
                  value={formData.ownerName}
                  onChange={handleInputChange}
                  required
                  placeholder="Adınız"
                />
              </div>

              <div className="form-group">
                <label htmlFor="ownerSurname">Sahip Soyadı *</label>
                <input
                  type="text"
                  id="ownerSurname"
                  name="ownerSurname"
                  value={formData.ownerSurname}
                  onChange={handleInputChange}
                  required
                  placeholder="Soyadınız"
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="phone">Telefon *</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  required
                  placeholder="(0555) 123 45 67"
                />
              </div>

              <div className="form-group">
                <label htmlFor="email">E-posta *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                  placeholder="ornek@email.com"
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="taxNumber">Vergi Numarası</label>
              <input
                type="text"
                id="taxNumber"
                name="taxNumber"
                value={formData.taxNumber}
                onChange={handleInputChange}
                placeholder="1234567890"
              />
            </div>
          </div>

          {/* Adres Bilgileri */}
          <div className="form-section">
            <h2>Adres Bilgileri</h2>

            <div className="form-group">
              <label htmlFor="address">Adres *</label>
              <textarea
                id="address"
                name="address"
                value={formData.address}
                onChange={handleInputChange}
                required
                placeholder="Tam adres bilgisi"
                rows="3"
              ></textarea>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="city">Şehir *</label>
                <input
                  type="text"
                  id="city"
                  name="city"
                  value={formData.city}
                  onChange={handleInputChange}
                  required
                  placeholder="İstanbul"
                />
              </div>

              <div className="form-group">
                <label htmlFor="district">İlçe *</label>
                <input
                  type="text"
                  id="district"
                  name="district"
                  value={formData.district}
                  onChange={handleInputChange}
                  required
                  placeholder="Kadıköy"
                />
              </div>

              <div className="form-group">
                <label htmlFor="postalCode">Posta Kodu</label>
                <input
                  type="text"
                  id="postalCode"
                  name="postalCode"
                  value={formData.postalCode}
                  onChange={handleInputChange}
                  placeholder="34000"
                />
              </div>
            </div>
          </div>

          {/* Hizmetler */}
          <div className="form-section">
            <h2>Sunulan Hizmetler</h2>
            <div className="services-grid">
              {availableServices.map(service => (
                <label key={service.name} className="service-checkbox">
                  <input
                    type="checkbox"
                    checked={formData.services.find(s => s.name === service.name) ? true : false}
                    onChange={() => handleServiceChange(service)}
                  />
                  <span className="checkmark"></span>
                  <div className="service-info">
                    <span className="service-name">{service.name}</span>
                    <span className="service-price">₺{service.price}</span>
                  </div>
                </label>
              ))}
            </div>
          </div>

          {/* Çalışma Saatleri */}
          <div className="form-section">
            <h2>Çalışma Saatleri</h2>
            <div className="working-hours">
              {Object.entries(days).map(([dayKey, dayName]) => (
                <div key={dayKey} className="day-schedule">
                  <div className="day-name">{dayName}</div>
                  <div className="day-controls">
                    <label className="closed-checkbox">
                      <input
                        type="checkbox"
                        checked={formData.workingHours[dayKey].closed}
                        onChange={(e) => handleWorkingHoursChange(dayKey, 'closed', e.target.checked)}
                      />
                      Kapalı
                    </label>
                    {!formData.workingHours[dayKey].closed && (
                      <>
                        <input
                          type="time"
                          value={formData.workingHours[dayKey].open}
                          onChange={(e) => handleWorkingHoursChange(dayKey, 'open', e.target.value)}
                        />
                        <span>-</span>
                        <input
                          type="time"
                          value={formData.workingHours[dayKey].close}
                          onChange={(e) => handleWorkingHoursChange(dayKey, 'close', e.target.value)}
                        />
                      </>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Açıklama */}
          <div className="form-section">
            <div className="form-group">
              <label htmlFor="description">İşletme Açıklaması</label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                placeholder="İşletmeniz hakkında kısa bir açıklama..."
                rows="4"
              ></textarea>
            </div>
          </div>

          <button type="submit" className="submit-btn">
            İşletmeyi Kaydet
          </button>
        </form>
      </div>
    </div>
  );
};

export default BusinessOwnerForm;
