import React, { useState, useEffect } from 'react';
import './BusinessDashboard.css';

const BusinessDashboard = () => {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('ALL');
  const [businessOwnerId, setBusinessOwnerId] = useState(1); // GeÃ§ici olarak 1 kullanÄ±yoruz

  // RandevularÄ± getir
  const fetchAppointments = async () => {
    try {
      setLoading(true);
      let url;
      
      if (filter !== 'ALL') {
        url = `http://localhost:8080/appointments/business/${businessOwnerId}/status/${filter}`;
      } else {
        url = `http://localhost:8080/appointments/business/${businessOwnerId}`;
      }
      
      const response = await fetch(url);
      if (response.ok) {
        const data = await response.json();
        setAppointments(data);
      } else {
        setError('Randevular yÃ¼klenirken hata oluÅŸtu');
      }
    } catch (err) {
      setError('BaÄŸlantÄ± hatasÄ±: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  // Randevu durumunu gÃ¼ncelle
  const updateAppointmentStatus = async (appointmentId, newStatus) => {
    try {
      const response = await fetch(`http://localhost:8080/appointments/${appointmentId}/status?status=${newStatus}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      
      if (response.ok) {
        // RandevularÄ± yeniden yÃ¼kle
        fetchAppointments();
        alert('Randevu durumu gÃ¼ncellendi!');
      } else {
        alert('Durum gÃ¼ncellenirken hata oluÅŸtu');
      }
    } catch (err) {
      alert('BaÄŸlantÄ± hatasÄ±: ' + err.message);
    }
  };

  // Component yÃ¼klendiÄŸinde randevularÄ± getir
  useEffect(() => {
    fetchAppointments();
  }, [filter, businessOwnerId]);

  // WebSocket baÄŸlantÄ±sÄ± (gerÃ§ek zamanlÄ± gÃ¼ncellemeler iÃ§in)
  useEffect(() => {
    // SockJS ve STOMP kullanarak WebSocket baÄŸlantÄ±sÄ±
    const SockJS = require('sockjs-client');
    const Stomp = require('stompjs');
    
    const socket = new SockJS('http://localhost:8080/ws');
    const stompClient = Stomp.over(socket);
    
    stompClient.connect({}, () => {
      console.log('WebSocket baÄŸlantÄ±sÄ± kuruldu');
      
      // Ä°ÅŸletme sahibine Ã¶zel bildirimler iÃ§in subscribe
      stompClient.subscribe(`/topic/notifications/${businessOwnerId}`, (message) => {
        const notification = JSON.parse(message.body);
        if (notification.type === 'NEW_APPOINTMENT') {
          alert('Yeni randevu alÄ±ndÄ±!');
          fetchAppointments();
        }
      });
    }, (error) => {
      console.error('WebSocket baÄŸlantÄ± hatasÄ±:', error);
    });
    
    return () => {
      if (stompClient && stompClient.connected) {
        stompClient.disconnect();
      }
    };
  }, [businessOwnerId]);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('tr-TR');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'PENDING': return '#ffa500';
      case 'CONFIRMED': return '#28a745';
      case 'COMPLETED': return '#6c757d';
      case 'CANCELLED': return '#dc3545';
      default: return '#007bff';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'PENDING': return 'Beklemede';
      case 'CONFIRMED': return 'OnaylandÄ±';
      case 'COMPLETED': return 'TamamlandÄ±';
      case 'CANCELLED': return 'Ä°ptal Edildi';
      default: return status;
    }
  };

  if (loading) {
    return <div className="loading">Randevular yÃ¼kleniyor...</div>;
  }

  if (error) {
    return <div className="error">Hata: {error}</div>;
  }

  return (
    <div className="business-dashboard">
      <header className="dashboard-header">
        <h1>Ä°ÅŸletme YÃ¶netim Paneli</h1>
        <p>RandevularÄ±nÄ±zÄ± yÃ¶netin ve takip edin</p>
      </header>

      <div className="dashboard-controls">
        <div className="filter-section">
          <label htmlFor="status-filter">Durum Filtresi:</label>
          <select 
            id="status-filter"
            value={filter} 
            onChange={(e) => setFilter(e.target.value)}
            className="filter-select"
          >
            <option value="ALL">TÃ¼mÃ¼</option>
            <option value="PENDING">Beklemede</option>
            <option value="CONFIRMED">OnaylandÄ±</option>
            <option value="COMPLETED">TamamlandÄ±</option>
            <option value="CANCELLED">Ä°ptal Edildi</option>
          </select>
        </div>
        
        <button 
          onClick={fetchAppointments}
          className="refresh-btn"
        >
          ðŸ”„ Yenile
        </button>
      </div>

      <div className="appointments-section">
        <h2>Randevular ({appointments.length})</h2>
        
        {appointments.length === 0 ? (
          <div className="no-appointments">
            <p>HenÃ¼z randevu bulunmuyor.</p>
          </div>
        ) : (
          <div className="appointments-grid">
            {appointments.map((appointment) => (
              <div key={appointment.id} className="appointment-card">
                <div className="appointment-header">
                  <span 
                    className="status-badge"
                    style={{ backgroundColor: getStatusColor(appointment.status) }}
                  >
                    {getStatusText(appointment.status)}
                  </span>
                  <span className="appointment-id">#{appointment.id}</span>
                </div>
                
                <div className="appointment-details">
                  <h3>{appointment.customerName}</h3>
                  <p><strong>Telefon:</strong> {appointment.customerPhone}</p>
                  <p><strong>Tarih:</strong> {formatDate(appointment.appointmentDate)}</p>
                  <p><strong>Hizmetler:</strong> {appointment.services?.join(', ')}</p>
                  <p><strong>Toplam Fiyat:</strong> â‚º{appointment.totalPrice}</p>
                  {appointment.notes && (
                    <p><strong>Notlar:</strong> {appointment.notes}</p>
                  )}
                </div>
                
                <div className="appointment-actions">
                  {appointment.status === 'PENDING' && (
                    <>
                      <button 
                        onClick={() => updateAppointmentStatus(appointment.id, 'CONFIRMED')}
                        className="btn btn-confirm"
                      >
                        Onayla
                      </button>
                      <button 
                        onClick={() => updateAppointmentStatus(appointment.id, 'CANCELLED')}
                        className="btn btn-cancel"
                      >
                        Ä°ptal Et
                      </button>
                    </>
                  )}
                  
                  {appointment.status === 'CONFIRMED' && (
                    <>
                      <button 
                        onClick={() => updateAppointmentStatus(appointment.id, 'COMPLETED')}
                        className="btn btn-complete"
                      >
                        Tamamla
                      </button>
                      <button 
                        onClick={() => updateAppointmentStatus(appointment.id, 'CANCELLED')}
                        className="btn btn-cancel"
                      >
                        Ä°ptal Et
                      </button>
                    </>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default BusinessDashboard;
